let data = rawFull

data = data.split("@")
for(let i = 0; i<data.length;i++ ){
    data[i]= data[i].split("")
}
console.log(data[0])

let L = data.length

function north(grid){
    for(let c=0; c<grid[0].length;c++){
        for(let r=0; r<grid.length;r++){
            if(grid[r][c]=="O"){
                //console.log("symbol: "+ data[r][c] + " at r: "+ r + " and c: "+ c )
                 let prevR = r-1 
                
                while(prevR>=0 && grid[prevR][c]=="."){
                    grid[prevR][c]="O"
                    grid[prevR+1][c]="."
                    prevR --
                }
            }
        }
    }
    return grid
}

function west(grid){
    for(let r=0;r<grid.length;r++){
        for(let c=0; c<grid[0].length;c++){
            if(grid[r][c]=="O"){
                let prevC = c - 1
                while(prevC>=0 && grid[r][prevC]=="."){
                    grid[r][prevC] = "O"
                    grid[r][prevC+1]="."
                    prevC --
                }
            }
        }
    }
    return grid
}

function south(grid){ 
    for(let c=0; c<grid[0].length; c++){
        for(let r= grid.length-1; r>=0; r--){
            if(grid[r][c]=="O"){
                let nextR = r+1
                while(nextR<grid.length && grid[nextR][c]=="."){
                    grid[nextR][c]="O"
                    grid[nextR-1][c]="."
                    nextR++
                }
            }
        }
    }
    return grid
}


function east(grid){
    for(let r=0; r<grid.length; r++){
        for(let c = grid[0].length-1;c>=0;c--){
            if(grid[r][c]=="O"){
                let nextC = c+1
                while(nextC<grid[0].length && grid[r][nextC]=="."){
                    grid[r][nextC] = "O"
                    grid[r][nextC-1]="."
                    nextC ++
                }
            }
        }
    }
    return grid
}




function cycle(){
    north(data)
    west(data)
    south(data)
    east(data)
}

