function totalLoad(grid){
    let total = 0 
    for(let r = 0; r < grid.length; r++){
        for(let c = 0; c < grid[0].length; c++){
            if(grid[r][c]=="O"){
                total += grid.length - r
            }
        }
    }
    console.log(total)
    return total
}




function displayRows(grid){
    console.log("---------------------------------------------")
    for(let i=0; i<grid.length;i++){
        console.log(grid[i])
    }
    console.log("---------------------------------------------")
}

function shortBoardState(grid){
    let short = ""
    for(let r=0; r<grid.length;r++){
        for(let c=0; c<grid[0].length; c++){
            if(grid[r][c]=="O"){
                short = short + r + c + ","
            }
        }
    }
    return short
}
